// Mini loader simulado para js-yaml
// Nota: Este es un simulador y debería reemplazarse con la librería original para producción
var jsyaml = {
    load: function(yaml) {
        if (!yaml || typeof yaml !== 'string') {
            console.warn("El YAML proporcionado es inválido o está vacío.");
            return {};
        }
        // Simulación de carga y parseo exitoso
        console.log("Simulando la carga de configuración desde YAML...");
        return {
            tema: "claro",
            idioma: "es",
            mostrar_imagenes: true,
            elementos_por_pagina: 10,
            animacion: "fade"
        };
    }
};
